import ColorThief from 'colorthief'
const colorThief = new ColorThief();

document.querySelectorAll(`#component-${primo.id} [data-set-color]`).forEach((item) => {
    const { setColor } = item.dataset
    const color = getColors(setColor)
    item.style.borderColor = color
})

function getColors(url) {
    var img = document.createElement("img"); 
    img.src = url
    img.crossOrigin = 'Anonymous'
    if (img.complete) {
    	return fetchColor(img)
    } else {
        img.addEventListener('load', function() {
    		return fetchColor(img)
        });
    }
    function fetchColor(img) {
        const [ dominant ] = colorThief.getPalette(img)
        const [ r, g, b ] = dominant
        return `rgb(${r},${g},${b})`
    }
}

